import React from "react"
import {useState} from "react"
import {useEffect } from 'react';
import {BrowserRouter as Router,Route,Routes,Link} from "react-router-dom";

const SearchBar = () => {
  const [message, setMessage] = useState('');
  var curprice=-9999;
  var buttonClick=false;
  const h1style={
		color:"#EA71E7"
	}
  const searchstyle={
  	padding: 5,
    borderRadius:"12px",
    height: "40px",
    width: "30%"
  }
  const buttonstyle={
  	padding: 5,
    borderRadius:"12px",
    height: "25px",
    width: "20%"
  }
  const handleChange = event => {
    setMessage(event.target.value);

    //console.log('value is:', event.target.value);
  };

  const handleClick =()=>
  {   
      buttonClick=true;

      fetch('http://localhost:5000/api/'+message)
         .then((res) => res.json())
         .then((data) => {
            console.log(data);
            curprice=data["price"]
            console.log(curprice)
         })
         .catch((err) => {
            console.log(err.message);
         });

  }
  if(!buttonClick)
  {
    return (
    <div>
      <input
        type="text"
        style={searchstyle}
        placeholder="Enter Ticker"
        name="symbol"
        onChange={handleChange}
        value={message}
      />
      <br/>
      <br/>
      <br/>
      <br/>
      <input
        type="button"
        value="Get Value"
        style={buttonstyle}
        onClick={handleClick}     
      />
    </div>
    );
  }
  else
  {
    return (
    <div>
      <h1 style={h1style}>Hope this works</h1>
    </div>
    );

  }
};

export default SearchBar;
